_satellite.pushAsyncScript(function(event, target, $variables){
  /*global window*/
/*
 ** these need to be independent rules but it became very cumbersome
 ** as marketing agencies were creating so many rules per url with very
 ** similar tag, therefore, I create a simple page rule to handle them all
 ** This way we have a single inject instead of multiple files/network calls if seq or not.
 **
 ** NOTE: This has to be seqential, otherwise, the domain/url that will be pulled
 ** will be from the iframe that is generated by Adobe.
 */

// pull out the path name as most rules are dependent on this
var pathName = window.s_parseUri().pathname;
if (pathName) {
    // based on path name
    switch (pathName) {
        case '/b/50-off-select-bibles/_/N-2gjf':
            // Sonja Balcer (TPI-183)
            window.r_injectIFrame('https://4921982.fls.doubleclick.net/activityi;src=4921982;type=offer0;cat=50off0;dc_lat=;dc_rdid=;tag_for_child_directed_treatment=;ord=' + window.r_getRandomOrd() + '?', null, null, 100);
            break;
        case '/b/books/bibles-christianity/_/N-29Z8q8Zsj2':
            // Sonja Balcer (TPI-183)
            window.r_injectIFrame('https://4921982.fls.doubleclick.net/activityi;src=4921982;type=sub-b0;cat=bnboo0;dc_lat=;dc_rdid=;tag_for_child_directed_treatment=;ord=' + window.r_getRandomOrd() + '?', null, null, 100);
            break;
        case '/b/select-hardcovers-2-for-30/_/N-2if8':
            // No ticket created yet by Christian
            window.r_injectIFrame('https://4921982.fls.doubleclick.net/activityi;src=4921982;type=offer0;cat=2for30;dc_lat=;dc_rdid=;tag_for_child_directed_treatment=;ord=' + window.r_getRandomOrd() + '?', null, null, 100);
            break;
        case '/b/favorite-paperbacks-buy-2-get-the-3rd-free/_/N-2ic8':
            // No ticket created yet by Christian
            window.r_injectIFrame('https://4921982.fls.doubleclick.net/activityi;src=4921982;type=offer0;cat=b2g3r0;dc_lat=;dc_rdid=;tag_for_child_directed_treatment=;ord=' + window.r_getRandomOrd() + '?', null, null, 100);
            break;
        case '/nook-blog/':
            // No ticket created yet by Christian
            window.r_injectIFrame('https://4921982.fls.doubleclick.net/activityi;src=4921982;type=blog0;cat=nookb0;dc_lat=;dc_rdid=;tag_for_child_directed_treatment=;ord=' + window.r_getRandomOrd() + '?', null, null, 100);
            break;
            // April 14th 2016
            //------------------------------------------------------------->
        case '/b/image-comics-buy-2-get-a-3rd-free/_/N-1gmj':
            // B2G3rd Image Comics
            window.r_injectIFrame('https://4921982.fls.doubleclick.net/activityi;src=4921982;type=offer0;cat=b2g3r000;dc_lat=;dc_rdid=;tag_for_child_directed_treatment=;ord=' + window.r_getRandomOrd() + '?', null, null, 100);
            break;
        case '/b/must-reads-in-paperback-buy-2-get-the-3rd-free/_/N-2ike':
            // B2G3rd Must Reads
            window.r_injectIFrame('https://4921982.fls.doubleclick.net/activityi;src=4921982;type=offer0;cat=b2g3r00;dc_lat=;dc_rdid=;tag_for_child_directed_treatment=;ord=' + window.r_getRandomOrd() + '?', null, null, 100);
            break;
        case '/b/who-was-series-buy-2-get-the-3rd-free/_/N-1p27':
            // B2G3rd Who Was
            window.r_injectIFrame('https://4921982.fls.doubleclick.net/activityi;src=4921982;type=offer0;cat=b2g3r001;dc_lat=;dc_rdid=;tag_for_child_directed_treatment=;ord=' + window.r_getRandomOrd() + '?', null, null, 100);
            break;
        case '/h/ebook-settlement':
            // Jeff Silverstein (BN_eBook Settlement)
            window.r_injectIFrame('https://4921982.fls.doubleclick.net/activityi;src=4921982;type=blog0;cat=bn_eb0;dc_lat=;dc_rdid=;tag_for_child_directed_treatment=;ord=' + window.r_getRandomOrd() + '?', null, null, 100);
            break;
        case '/h/makerfaire':
            window.r_injectIFrame('https://4921982.fls.doubleclick.net/activityi;src=4921982;type=maker0;cat=maker0;dc_lat=;dc_rdid=;tag_for_child_directed_treatment=;ord=' + window.r_getRandomOrd() + '?', null, null, 100);
            break;
        case '/h/makerfaire/signup':
            window.r_injectIFrame('https://4921982.fls.doubleclick.net/activityi;src=4921982;type=maker0;cat=maker00;dc_lat=;dc_rdid=;tag_for_child_directed_treatment=;ord=' + window.r_getRandomOrd() + '?', null, null, 100);
            break;
    }
}
});
